# https://data.cityofchicago.org/Health-Human-Services/Census-Data-Selected-socioeconomic-indicators-in-C/kn9c-c2s2/about_data

CREATE DATABASE IF NOT EXISTS chicago;
USE chicago;
DROP TABLE IF EXISTS chicago_socioeconomic;

CREATE TABLE chicago_socioeconomic (
	community_area_number int, 
	community_area_name varchar(22), 
	percent_of_housing_crowded float, 
	percent_housholds_below_poverty float, 
	percent_aged_16_above_unemployed float, 
	percent_aged_25_above_without_high_school_diploma float, 
	percent_aged_under_18_or_over_64 float, 
	per_capita_income int, 
	hardship_index int
);
